from back_gammon_structure import BackGamonfrom gui_structure import BackGamonIMGfrom process_img import get_circles_positions_and_colorfrom calibration import calibration# import timeimport numpy as npimport cv2from mss import mssimport osbase_path = "../data/games/"def take_screen(sct, bounding_box, game_save_path):     # print("save screen at : "+ game_save_path)    # bounding_box = {'top': 100, 'left': 0, 'width': 400, 'height': 300}    sct_img = sct.grab(bounding_box)    open_cv_screen = np.array(sct_img)    cv2.imwrite(game_save_path, open_cv_screen)if __name__ == "__main__":    sct = mss()    arr = os.listdir(base_path)    game_id = len(arr)    if(".DS_Store" in arr):        game_id -= 1    game_save_path = base_path + str(game_id)    os.mkdir(game_save_path)    bounding_box_all = {'top': 0, 'left': 0, 'width': 1440, 'height': 900}    all_screen_img_path = game_save_path+"/all_screen.png"    take_screen(sct, bounding_box_all, all_screen_img_path)    haut_gauche, bas_droite = calibration(all_screen_img_path)    print("calibration haut_gauche at {}".format(haut_gauche))    print("calibration bas_droite at  {}".format(bas_droite))    width = (bas_droite[0] - haut_gauche[0]) / 2    height = (bas_droite[1] - haut_gauche[1]) / 2    top = int(haut_gauche[1]/2)    left = int(haut_gauche[0]/2)    bounding_box = {'top': top , 'left': left, 'width': width , 'height': height}    # print(bounding_box)    # time.sleep(4)    bg = BackGamon('blanc')    # debug_img = cv2.imread(game_save_path + "/game_screen.png")    bg_img = BackGamonIMG()    # bg_img.debug_by_img()    i = 0    while(i < 10):        img_path = game_save_path + "/game_screen.png"        take_screen(sct, bounding_box, img_path)        df_cicles, resized_img, scale = get_circles_positions_and_color(img_path)        # print("------------ scale factor -----------------")        # print(scale)        # print(resized_img.shape)        # print("------------ scale factor -----------------")        bg_img.set_bar_position(resized_img.shape[1], resized_img.shape[0])        bg_img.set_debug_img(resized_img)        bg_img.debug_by_img()        for pion_id in range(len(df_cicles)):            df_line = df_cicles.iloc[pion_id]            fleche_id = bg_img.getFlechePlusPres(df_line['center'], df_line['verticality'])            # print("fleche_id")            # print(fleche_id)            if(fleche_id is None):                continue            if(fleche_id == -1):                bg.add_bar(df_line['color'])            else:                bg.add_fleche_by_id(fleche_id, df_line['color'])        bg.debugPlateau()        bg.reset_board()        i += 1# img_path = "../data/test/beginning.png"# df_cicles = get_circles_positions_and_color(img_path)# bg = BackGamon('blanc')# bg_img = BackGamonIMG()# for pion_id in range(len(df_cicles)):#     # print(df_cicles.iloc[pion_id])#     df_line = df_cicles.iloc[pion_id]#     fleche_id = bg_img.getFlechePlusPres(df_line['center'], df_line['verticality'])#     if(fleche_id == -1):#         bg.add_bar(df_line['color'])#     else:#         bg.add_fleche_by_id(fleche_id, df_line['color'])# bg.debugPlateau()